mapper.select:
SELECT id, shopName, shopPhone, status, shopOpenTime, shopCloseTime, createdAt, updatedAt
FROM shop
WHERE id = #{id}

mapper.update:
UPDATE shop
SET shopName = #{shopName}, shopPhone = #{shopPhone}, shopOpenTime = #{shopOpenTime}, shopCloseTime = #{shopCloseTime}
WHERE id = #{id}

UPDATE shop
SET status = #{status}
WHERE id = #{id}

